/**
 * Call `addEventListener` on the given `EventTarget` instance and return a callback that removes
 * that listener.
 *
 * @category Listen
 */
export function listenTo(target, type, listener, options) {
    target.addEventListener(type, listener, options);
    return () => {
        return target.removeEventListener(type, listener, options);
    };
}
/**
 * A wrapper for `globalThis.addEventListener`
 *
 * @category Listen
 */
export function listenToGlobal(type, listener, options) {
    return listenTo(globalThis, type, listener, options);
}
